import React from 'react'

const ImageCard = ({imageData}) => {
    console.log(imageData);
    const { title, pathalias, ownername, id, farm, server, secret, description } = imageData;
    const imgUrl = `https://farm${farm}.staticflickr.com/${server}/${id}_${secret}.jpg`;
    const profileUrl = `https://www.flickr.com/photos/${pathalias}/`;
    const flickrPhotoUrl = `${profileUrl}/${id}/`;
    console.log(description)

    return (
        <div className="photo-card">
           <div className="img-container"><img src={imgUrl} alt=""/></div>
           <h2><a href={flickrPhotoUrl} target="_blank">{title}</a></h2>
           <span>by <a href={profileUrl} target="_blank">{ownername}</a></span>

           <p>{!description ? "whatever you want" : description}</p>
        </div>
    )
}

export default ImageCard
