const App = () => {
  const [data, setData] = useState(null);

  const fetchFlickrdata = () => {
    const pop = fetch('https://api.flickr.com/services/rest/?method=flickr.galleries.getPhotos&api_key=2a0b9c81a6daa39ee08fb2c9569a4d64&gallery_id=66911286-72157647277042064&format=json&nojsoncallback=1')
    .then(res => res.json())
    .then(data => setData(data?.photos?.photo))
  }

  useEffect(() => {
    if (data) return data;

    if (!data) return fetchFlickrdata();
  }, [])

